contracts/EnsManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";

interface INameWrapper {
    function setSubnodeOwner(
        bytes32 parentNode,
        string calldata label,
        address owner,
        uint32 fuses,
        uint64 expiry
    ) external returns (bytes32);
}

contract ENSManager is Ownable(msg.sender) {
    INameWrapper public nameWrapper;
    bytes32 public parentNode;
    string public baseDomain;

    mapping(string => address) public subnameOwners;
    mapping(address => string) public addressToSubname;

    event SubnameRegistered(address indexed user, string subname);

    constructor(address _nameWrapper, bytes32 _parentNode, string memory _baseDomain) {
        nameWrapper = INameWrapper(_nameWrapper);
        parentNode = _parentNode;
        baseDomain = _baseDomain;
    }

    /** @notice Check if a subname is available */
    function isAvailable(string calldata name) public view returns (bool) {
        return subnameOwners[name] == address(0);
    }

    /** @notice Register a free subname under your parent domain */
    function registerSubname(string calldata name) external returns (string memory) {
        require(isAvailable(name), "Subname already taken");

        // Map ownership
        subnameOwners[name] = msg.sender;
        addressToSubname[msg.sender] = name;

        // Set fuses for a "forever" subname
        uint32 fuses = 65536; // CAN_EXTEND_EXPIRY burned
        uint64 expiry = type(uint64).max;

        // Call NameWrapper to create the actual ENS subname
        nameWrapper.setSubnodeOwner(parentNode, name, msg.sender, fuses, expiry);

        string memory fullName = string(abi.encodePacked(name, ".", baseDomain));
        emit SubnameRegistered(msg.sender, fullName);
        return fullName;
    }

    /** @notice Get full ENS name for a user */
    function getFullName(address user) external view returns (string memory) {
        string memory name = addressToSubname[user];
        if (bytes(name).length == 0) return "";
        return string(abi.encodePacked(name, ".", baseDomain));
    }
}

contracts/JobFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

interface IProfile {
    function getProfile(
        address _user
    )
        external
        view
        returns (
            address user,
            string memory ensName,
            string memory metadataURI,
            bool exists,
            bool hasEFP,
            uint256 reputation
        );
}

contract JobFactory is Ownable {
    enum JobStatus {
        Open,
        Assigned,
        Completed,
        Cancelled
    }

    struct Job {
        uint256 id;
        address client;
        address worker;
        string title;
        string metadataURI;
        uint256 budget;
        uint256 deadline;
        JobStatus status;
    }

    IERC20 public coreToken;
    IProfile public profileContract;

    uint256 public jobCount;
    mapping(uint256 => Job) public jobs;

    event JobCreated(uint256 indexed jobId, address indexed client, uint256 budget);
    event JobAssigned(uint256 indexed jobId, address indexed worker);
    event JobCompleted(uint256 indexed jobId, address indexed worker);
    event JobCancelled(uint256 indexed jobId);

    constructor(address _coreToken, address _profileContract) Ownable(msg.sender) {
        coreToken = IERC20(_coreToken);
        profileContract = IProfile(_profileContract);
    }

    /// @notice Client posts a job with budget locked in escrow
    function createJob(string calldata title, string calldata metadataURI, uint256 budget, uint256 deadline) external {
        require(budget > 0, "Invalid budget");
        require(deadline > block.timestamp, "Deadline must be future");

        // transfer budget to contract
        require(coreToken.transferFrom(msg.sender, address(this), budget), "Funding failed");

        jobCount++;
        jobs[jobCount] = Job({
            id: jobCount,
            client: msg.sender,
            worker: address(0),
            title: title,
            metadataURI: metadataURI,
            budget: budget,
            deadline: deadline,
            status: JobStatus.Open
        });

        emit JobCreated(jobCount, msg.sender, budget);
    }

    /// @notice Worker accepts job
    function acceptJob(uint256 jobId) external {
        Job storage job = jobs[jobId];
        require(job.status == JobStatus.Open, "Job not open");
        require(job.client != msg.sender, "Client cannot take own job");

        // check profile exists
        (, , , bool exists, , ) = profileContract.getProfile(msg.sender);
        require(exists, "Worker must have profile");

        job.worker = msg.sender;
        job.status = JobStatus.Assigned;

        emit JobAssigned(jobId, msg.sender);
    }

    /// @notice Client marks job as completed → releases payment
    function completeJob(uint256 jobId) external {
        Job storage job = jobs[jobId];
        require(job.status == JobStatus.Assigned, "Not assigned");
        require(job.client == msg.sender, "Only client can complete");

        // ✅ update state first
        job.status = JobStatus.Completed;
        address worker = job.worker;
        uint256 amount = job.budget;

        // then external call
        require(coreToken.transfer(worker, amount), "Payment failed");

        emit JobCompleted(jobId, worker);
    }

    /// @notice Client cancels before worker accepts
    function cancelJob(uint256 jobId) external {
        Job storage job = jobs[jobId];
        require(job.status == JobStatus.Open, "Job not open");
        require(job.client == msg.sender, "Only client can cancel");

        job.status = JobStatus.Cancelled;
        uint256 refund = job.budget;
        address client = job.client;

        require(coreToken.transfer(client, refund), "Refund failed");

        emit JobCancelled(jobId);
    }

    function getJob(uint256 jobId) external view returns (Job memory) {
        require(jobId > 0 && jobId <= jobCount, "Invalid jobId");
        return jobs[jobId];
    }

    function getAllJobs() external view returns (Job[] memory) {
        Job[] memory all = new Job[](jobCount);
        for (uint256 i = 1; i <= jobCount; i++) {
            all[i - 1] = jobs[i];
        }
        return all;
    }

    function getOpenJobs() external view returns (Job[] memory) {
        // count first
        uint256 count = 0;
        for (uint256 i = 1; i <= jobCount; i++) {
            if (jobs[i].status == JobStatus.Open) {
                count++;
            }
        }
        // fill array
        Job[] memory openJobs = new Job[](count);
        uint256 index = 0;
        for (uint256 i = 1; i <= jobCount; i++) {
            if (jobs[i].status == JobStatus.Open) {
                openJobs[index] = jobs[i];
                index++;
            }
        }
        return openJobs;
    }
}
